import React from 'react';
import './Blogs.css'; 

const Blogs = () => {

  const redirectoblogs = () => {
    window.location.href = '/blogs'; 
  }
  const blogPosts = [
    {
      image: 'data:image/jpeg;base64,',
      title: 'Top Insurance Certifications in India to Accelerate Your Career',
      date: 'Sep 1, 2023',
      content: '15 Must-Know Insurance Certifications for Indian Professionals. In today\'s competitive insurance industry in India, gaining specialised certifications can give professionals a...',
    },
    {
      image: 'data:image/jpeg;base64,',
      title: 'What is Insurance: Definition, Benefits, and Types',
      date: 'Jun 10, 2023 | Insurance',
      content: 'What is Coverage in Insurance  Insurance is a vital aspect of our lives, providing financial protection against unforeseen circumstances. One of the fundamental One of the fundamentalOne of the fundamentalOne of the fundamental...',
    },
    {
      image: "data:image/jpeg;base64,",
      title: 'Your Guide to Building a Career in Sales',
      date: 'May 3, 2023 | Career Options',
      content: 'Guide to Building a Career in Sales Sales serve as the backbone of any organization, and a profession can provide tremendous benefits. Whether you are already contemplating...',
    },
    // Add more blog posts as needed
  ];

  return (
    <div className='blogs_section'>
      <h3>Our Blogs</h3>

      <div className="blog-container">
        {blogPosts.map((post, index) => (
          <div className="blog-post" key={index}>
            <img src={post.image} alt={post.title} />
            <div className='contents_box'>
              <h4>{post.title}</h4>
              <p>{post.date}</p>
              <p>{post.content}</p>
              <button>Read more</button>
            </div>

          </div>
        ))}
      </div>
      <div className='viewall'>
        <button onClick={redirectoblogs}>View all</button>
      </div>
    </div>
  );
};

export default Blogs;
